mapper.select:
select * 
from (select rownum rnum, b.*
	  from (select *
	  		from adminboard
			order by adNum desc
	  ) b
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update adminboard
set adTitle = #{adTitle}, adContent = #{adContent}, QcateId = #{QcateId}
where adNum = #{adNum}

mapper.select:
select * from adminboard
where adNum = #{num}					
and adPassword = #{pass}
