mapper.select:
select * 
from (select rownum rnum, b.*
	  from (select *
	  		from adminboard
	  		where key = 'FAQ'
	  		and adState = 'Y'
			order by adNum desc
	  ) b
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update adminboard
set adTitle = #{adTitle}, adContent = #{adContent}, QcateId = #{QcateId}
where adNum = #{adNum}

mapper.select:
select adNum, adPassword from adminboard
where adNum = #{num}					
and adPassword = #{pass}

select * 
from (select rownum rnum, b.*
	  from (select *
	  		from adminboard
	  		where key = 'QNA'
	  		and adState = 'Y'
			order by adNum desc
	  ) b
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

select * from(select rownum rnum, q.*
			  from( select qnaReplyNum, adNum, adminId,
			  	    qnaReplyContent, qnaReplyDate, qnaReplyUpdate
					from QNAREPLY
					where adNum = #{adNum}
					and qnaReplyState = 'Y'
					order by qnaReplyNum desc
				  ) q
			  where rownum <= #{end}
			  )
where rnum between #{start} and #{end}

select count(*) from QNAREPLY
where adNum = #{adNum}
and qnaReplyState = 'Y'

mapper.update:
update QNAREPLY
set qnaReplyContent = #{qnaReplyContent}
where qnaReplyNum = #{qnaReplyNum}

update QNAREPLY
set qnaReplyState = 'N'
where qnaReplyNum = #{qnaReplyNum}

