mapper.select:
select * 
from (select rownum rnum, a.*
	  from (select adminId, adminName, adminDept, adminClass, authName
	  		from admin
	  		order by authName
	  ) a
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update admin
set authName = #{authName}
where adminId = #{adminId}

mapper.select:
select * 
from (select rownum rnum, s.*
	  from (select waitNum, userId, waitPhone, waitEmail, waitDate
	  		from SellerWaiting
	  		where waitState = 'W'
	  		order by waitDate desc
	  ) s
	  where rownum <= #{end}
)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update sellerwaiting
set waitState = 'Y'
where userId = #{userId}

update sellerwaiting
set waitState = 'N'
where userId = #{userId}

mapper.select:
select * 
from (SELECT rownum rnum, b.*, statusid
	  FROM (select sickid, count(*) as sumcount
		  	from (select distinct sickid, warnnum
				  from boardwarn
				  union all
				  select distinct sickid, warnnum
				  from replywarn
			      )
		    group by sickid
	       ) b
	 INNER JOIN itda_user iu ON b.sickId = iu.userId
	 where rownum <= #{end}
	 )
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update itda_user
set statusid = 2
where userId = #{userId}

update itda_user
set statusid = 3
where userId = #{userId}

update itda_user
set statusid = 1
where userId = #{userId}
