mapper.update:
	update BoardReply
	set REPLYSEQ = replySeq + 1
	WHERE REPLYREF = #{replyRef}
    and REPLYSEQ >
    #{replySeq}

update BoardReply
set REPLYCONTENT = #{replyContent}
where REPLYNUM = #{replyNum}

mapper.select:
SELECT COUNT(*)
FROM boardreply
WHERE boardNum = #{boardNum}

SELECT COUNT(*)
FROM boardreply
WHERE boardNum = #{boardNum}
